# GameManager.gd - This is a NODE (behavior and scene management)
# Nodes are for behavior, scene tree management, and processing
extends Node
class_name GameManager

@export var game_state: GameState  # Resource reference
@export var board: TicTacToeBoard   # Node reference

func _ready():
    # Create or load the game state resource
    if not game_state:
        game_state = GameState.new()

    # Connect to board signals (node communication)
    if board:
        board.cell_clicked.connect(_on_cell_clicked)

func _on_cell_clicked(position: int):
    # Use the resource to track game state
    var success = game_state.make_move(position, game_state.player_turn)

    if success:
        # Update the node visuals based on resource data
        board.update_cell_display(position, game_state.board_state[position])

        if game_state.game_over:
            show_game_over_screen()

func save_game():
    # Resources can be easily saved to disk
    ResourceSaver.save(game_state, "user://savegame.tres")

func load_game():
    # Resources can be easily loaded from disk
    var loaded_state = ResourceLoader.load("user://savegame.tres") as GameState
    if loaded_state:
        game_state = loaded_state
        # Update node visuals based on loaded resource data
        board.restore_from_state(game_state.board_state)
