extends Node2D
class_name TicTacToeBoard

# The board is the parent, cells are children
@export var cell_scene: PackedScene
@export var board_size: Vector2 = Vector2(3, 3)
@export var cell_spacing: float = 100.0

var cells: Array[Array] = []

func _ready():
    create_board()

func create_board():
    # Create a 3x3 grid of cells as child nodes
    for row in range(board_size.y):
        var cell_row: Array[Node] = []
        for col in range(board_size.x):
            # Instantiate cell as child node
            var cell = cell_scene.instantiate()
            add_child(cell)

            # Position relative to parent (this board)
            var pos = Vector2(col * cell_spacing, row * cell_spacing)
            cell.position = pos

            # Store reference for game logic
            cell_row.append(cell)

            # Connect signals (child to parent communication)
            cell.cell_clicked.connect(_on_cell_clicked.bind(row, col))

        cells.append(cell_row)

func _on_cell_clicked(row: int, col: int):
    print("Cell clicked at: ", row, ", ", col)
    # Game logic here - the parent manages game state
    # while children (cells) only handle their own display
