extends Control
class_name TicTacToeCell

# Define custom signal - this is the "publish" part of publish-subscribe
signal cell_clicked

@export var cell_size: Vector2 = Vector2(90, 90)
@export var background_color: Color = Color.WHITE
@export var hover_color: Color = Color.LIGHT_GRAY

var cell_state: String = ""  # "", "X", or "O"
var is_occupied: bool = false

func _ready():
    # Set up the cell appearance
    custom_minimum_size = cell_size

    # Connect built-in signals to our custom logic
    mouse_entered.connect(_on_mouse_entered)
    mouse_exited.connect(_on_mouse_exited)
    gui_input.connect(_on_gui_input)

func _on_gui_input(event: InputEvent):
    if event is InputEventMouseButton:
        if event.pressed and event.button_index == MOUSE_BUTTON_LEFT:
            if not is_occupied:
                # Emit our custom signal - we don't know who's listening!
                # This is loose coupling - the cell doesn't need to know
                # about the board, game manager, or any other systems
                cell_clicked.emit()

func _on_mouse_entered():
    if not is_occupied:
        modulate = hover_color

func _on_mouse_exited():
    modulate = Color.WHITE

func set_cell_value(value: String):
    # Called by parent/game manager to update cell
    cell_state = value
    is_occupied = true
    # Update visual representation here
    queue_redraw()

func _draw():
    # Draw the cell background and content
    draw_rect(Rect2(Vector2.ZERO, size), background_color)
    if cell_state != "":
        var font = ThemeDB.fallback_font
        var font_size = 48
        var text_pos = size / 2 - font.get_string_size(cell_state, 0, -1, font_size) / 2
        draw_string(font, text_pos, cell_state, 0, -1, font_size, Color.BLACK)
